import React from 'react';

function Products() {
  const products = [
    {
      id: 1,
      name: 'Product 1',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed euismod turpis quis enim pretium tristique.',
      price: '$19.99',
      imageUrl: 'data:image/jpeg;base64,'
    },
    {
      id: 2,
      name: 'Product 2',
      description: 'Phasellus non posuere nunc. Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia curae.',
      price: '$29.99',
      imageUrl: 'data:image/jpeg;base64,'
    },
    {
      id: 3,
      name: 'Product 3',
      description: 'Integer posuere justo vitae dui malesuada, quis lacinia mauris volutpat. Duis et nunc vitae mauris rhoncus rhoncus.',
      price: '$39.99',
      imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQZzo_8U_RnFjJ-BK-eDl9YK1TR0xRNNKUGmw&usqp=CAU'
    }
  ];

  const cardStyle = {
    maxWidth: '300px',
    margin: '20px',
    padding: '15px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    borderRadius: '8px',
    backgroundColor: '#fff',
    textAlign: 'center'
  };

  const imageStyle = {
    minHeight: '400px',
    overflow: 'hidden',
    width: '100%',
    height: 'auto',
    borderRadius: '8px'
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
      {products.map(product => (
        <div key={product.id} style={cardStyle}>
          <img src={product.imageUrl} alt={product.name} style={imageStyle} />
          <h3>{product.name}</h3>
          <p>{product.description}</p>
          <p>{product.price}</p>
          <button>Add to Cart</button>
        </div>
      ))}
    </div>
  );
}

export default Products;
